const INITIAL_STATE = {
  isSignedIn: null,
};

export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case 'SIGN_IN':
      console.log('signing in');
      return { ...state, isSignedIn: true };
    case 'SIGN_OUT':
      console.log('signing out');
      return { ...state, isSignedIn: false };
    default:
      return state;
  }
};
